  products: [],
  setProducts: () => null,
})

export const ProductsProvider = ({ children }) => {
  const [products, setProducts] = useState(PRODUCTS);
  const value = { products };
 
 
  return <ProductContext.Provider value={value}>{children}</ProductContext.Provider>

};
