import { useEffect, } from 'react'
import { getRedirectResult } from 'firebase/auth'
import { auth, signInWithGooglePopup, createUserDocumentFromAuth, signInWithGoogleRedirect } from '../../utils/firebase/firebase.utils'

import './sign-in.styles.scss'

const SignIn = () => {

  useEffect(() => {
    const featchData = async() => {
      const response = await getRedirectResult(auth);
      return response;
    }; 
    featchData()
  }, []);
  const logGoogleUser = async () => {
    const { user } = await signInWithGooglePopup();
    const userDocRef = await createUserDocumentFromAuth(user);
  }

  return(
    <div className='container mx-auto grid grid-cols-1 lg:grid-cols-2 gap-2 px-1'>
      <div className='lg:col-span-2'>
        <h1 className='pb-3 text-center'>Sign In Page</h1>
      </div>
      <div className= 'bg-black h-80'></div>
      <div className='flex justify-center'>
        <button type="button" className='login-with-google-btn whitespace-nowrap ' onClick={logGoogleUser}>Sign in with Google Popup</button>
        <button type="button" className='login-with-google-btn whitespace-nowrap ' onClick={signInWithGoogleRedirect}>Sign in with Google Redirect</button>
      </div>
    </div>
  )
};

export default SignIn;
