import { useState } from 'react';
import { createAuthUserWithEmailAndPassword } from '../../utils/firebase/firebase.utils'
import './sign-up-form.styles.scss'

const defaultFormFields = {
  displayName: '',
  email: '',
  password: '',
  confirmPassword:'',
}

const SignUpForm = () => {
  const [formFields, setFormFields] = useState(defaultFormFields);
  const { displayName, email, password, confirmPassword } = formFields; 

  console.log(formFields);

  const handleSubmit = async (event) => {
    event.preventDefult();

    if(password !== confirmPassword){
      alert("password do not match!");
      return;
    }
    
    try {
      const respone = await createAuthUserWithEmailAndPassword(email, password);
    } catch (error) {
      
    }

  }

  const handleChange = (event) => {

    const { name, value } = event.target;
    setFormFields({...formFields, [name]: value })

  };

  return(
    <div className='fontFamily'>
      <h1 className="pb-2">Sign up with your email and password</h1>
      
      <form className="grid grid-col-4 gap-2" onSubmit={handleSubmit}>

        <label>Display Name</label>
        <input name='displayName' type="text" className="border border-spacing-1" onChange={handleChange} value={displayName} required></input>

        <label>Email</label>
        <input name='email' type="email" onChange={handleChange}  value={email} required></input>

        <label>Password</label>
        <input name='password' type="password" onChange={handleChange} value={password} required></input>

        <label>Comfirm Password</label>
        <input name='confirmPassword' type="password" onChange={handleChange} value={confirmPassword}  required></input>

        <div className="flex justify-center">
          <button className="signUpButton fontFamily bg-blue-500 hover:bg-blue-700 font-bold py-2 px-4 rounded w-2/4" type="submit">Sign Up</button>
        </div>

      </form>
      
    </div>
    
  )
};

export default SignUpForm;
